    query ALL_ITEMS_QUERY {
        items {
            id
            title
            price
            description
            image
            largeImage
        }
    }
`;


class Items extends Component {
    render() {
        return (
            <div>
                <p>Items!</p>
                <Query query={ALL_ITEMS_QUERY}>
                    {(payload) => {
                        console.log(payload);
                        return <p>Hey I'm the child of query</p>;
                    }}
                </Query>
            </div>
        );
    }
}
export default Items;
